function agnoPlotting_100D(bigY, mmNet, pcaNet, ts_idx ,iterations)yT = bigY(:, ts_idx-1);cT = pcaNet.bigC(:, ts_idx-1);figure(1)if pcaNet.learning == 1    plot(mmNet.allErrors(1:ts_idx), 'r-');else    plot(mmNet.allErrors(1:ts_idx), 'k-');end% hold on;% refline(0, pcaNet.sigmaThresh);% hold off;title(['Total Error over Time - ts: ' num2str(ts_idx)])xlabel('Timestep')ylabel('Error')xlim([0 iterations])ylim([0 500])pause(1e-5)