function seqCD_plotter_v6(cdNet, mmNet, ts_idx, winSize, plotInd, figNumber)thisCD = cdNet.totalActMat(:,ts_idx-(winSize-1):ts_idx);thisTotalMM = mmNet.totalSigMat;pattIn_mm = mmNet.input(:, ts_idx-(winSize-1):ts_idx);xplot = thisCD(flipud(plotInd),:); % from early (top) to late (bottom)figure(figNumber);subplot(4,1, [1 2])imagesc(xplot)title(['Activity - ts: ' num2str(ts_idx-(winSize-1)) '-' num2str(ts_idx)])% subplot(412)% imagesc(pattIn_cd)% title('CD Input')subplot(413)plot(thisTotalMM(ts_idx-(winSize-1):ts_idx))ylim([-0.5 10])title('Mismatch')subplot(414)imagesc(pattIn_mm)title('MM Input')